mapper.select:
SELECT ct.car_type_code, ct.car_type_name, cc1.common_name AS fuel_type, cc2.common_name AS seating_capacity, rc.model_year, rc.car_code, ct.car_image_name, cc3.common_name AS car_manufacturer, cc4.common_name AS car_type_category, b.branch_name, b.branch_latitude, b.branch_longitude, cc3.brand_image_name, rr.rental_rate, rr.rental_discount_rate FROM rental_cars rc JOIN car_types ct ON rc.car_type_code = ct.car_type_code JOIN common_codes cc1 ON cc1.common_code = ct.fuel_type AND cc1.common_kind_code = 'fuel_type' JOIN common_codes cc2 ON cc2.common_code = ct.seating_capacity AND cc2.common_kind_code = 'seating_capacity' JOIN common_codes cc3 ON cc3.common_code = ct.car_manufacturer AND cc3.common_kind_code = 'car_manufacturer' JOIN common_codes cc4 ON cc4.common_code = ct.car_type_category AND cc4.common_kind_code = 'car_type_category' JOIN branchs b on b.branch_code = rc.branch_code JOIN rental_rates rr on rr.car_type_code = rc.car_type_code
AND b.branch_name = #{branchName}
AND cc1.common_name = #{fuelType}
AND cc4.common_name = #{carTypeCategory}
AND cc3.common_name = #{carManufacturer}
AND cc2.common_name = #{seatingCapacity}
AND NOT EXISTS ( SELECT 1 FROM reservations r WHERE r.car_code = rc.car_code AND ( STR_TO_DATE(r.rental_date, '%Y%m%d') <= STR_TO_DATE(#{returnDate}, '%Y%m%d') AND STR_TO_DATE(r.return_date, '%Y%m%d') >= STR_TO_DATE(#{rentalDate}, '%Y%m%d') ) ) AND (#{returnDate} - #{rentalDate}) * 24 >= rr.rental_start_hours AND (#{returnDate} - #{rentalDate}) * 24 <=rr.rental_end_hours

SELECT COUNT(*) FROM rental_cars rc JOIN car_types ct ON rc.car_type_code = ct.car_type_code JOIN common_codes cc1 ON cc1.common_code = ct.fuel_type AND cc1.common_kind_code = 'fuel_type' JOIN common_codes cc2 ON cc2.common_code = ct.seating_capacity AND cc2.common_kind_code = 'seating_capacity' JOIN common_codes cc3 ON cc3.common_code = ct.car_manufacturer AND cc3.common_kind_code = 'car_manufacturer' JOIN common_codes cc4 ON cc4.common_code = ct.car_type_category AND cc4.common_kind_code = 'car_type_category' JOIN branchs b on b.branch_code = rc.branch_code
AND b.branch_name = #{branchName}
AND cc1.common_name = #{fuelType}
AND cc4.common_name = #{carTypeCategory}
AND cc3.common_name = #{carManufacturer}
AND cc2.common_name = #{seatingCapacity}
AND NOT EXISTS ( SELECT 1 FROM reservations r WHERE r.car_code = rc.car_code AND ( STR_TO_DATE(r.rental_date, '%Y%m%d') <= STR_TO_DATE(#{returnDate}, '%Y%m%d') AND STR_TO_DATE(r.return_date, '%Y%m%d') >= STR_TO_DATE(#{rentalDate}, '%Y%m%d') ) )
