mapper.select:
SELECT r.reservation_code AS reservationCode, u.user_name AS userName, rl.branch_name AS branchName, r.rental_date AS rentalDate, r.return_date AS returnDate, ct.car_type_name AS carType, CONCAT( 'Rental Period: ', r.rental_date, ' to ', r.return_date, ' (', DATEDIFF(r.return_date, r.rental_date), ' days)' ) AS rentalPeriod, r.payment_amount AS paymentAmount FROM reservations r JOIN users u ON r.user_code = u.user_code JOIN branchs rl ON r.rental_location = rl.branch_code JOIN rental_cars rc ON r.car_code = rc.car_code JOIN car_types ct ON rc.car_type_code = ct.car_type_code WHERE 1=1
AND rl.branch_name = #{branchName}
AND r.rental_date = DATE_FORMAT(#{rentalDate}, '%Y%m%d')
AND u.user_name = #{userName}
ORDER BY r.rental_date DESC LIMIT #{offset}, #{pageSize}
